import React, { useState } from "react";
import Tarjeta from "./Tarjeta";
import "./vocabulario.css";

function Vocabulario() {
  const [categoria, setCategoria] = useState("vocabulario");

  const vocabulario = {
    verbos: [
      { palabraEsp: "Hablar", palabraSue: "Tala" },
      { palabraEsp: "Comer", palabraSue: "Äta" },
      { palabraEsp: "Estudiar", palabraSue: "Studera" },
    ],
    adjetivos: [
      { palabraEsp: "Grande", palabraSue: "Stor" },
      { palabraEsp: "Pequeño", palabraSue: "Liten" },
      { palabraEsp: "Feliz", palabraSue: "Glad" },
    ],
    sustantivos: [
      { palabraEsp: "Perro", palabraSue: "Hund", tipo: "en" },
      { palabraEsp: "Libro", palabraSue: "Bok", tipo: "en" },
      { palabraEsp: "Mesa", palabraSue: "Bord", tipo: "ett" },
    ],
  };

  const renderCategoria = () => {
    switch (categoria) {
      case "verbos":
        return (
          <div>
            <h3>Verbos</h3>
            {vocabulario.verbos.map((item, index) => (
              <Tarjeta
                key={index}
                palabraEsp={item.palabraEsp}
                palabraSue={item.palabraSue}
              />
            ))}
          </div>
        );
      case "adjetivos":
        return (
          <div>
            <h3>Adjetivos</h3>
            {vocabulario.adjetivos.map((item, index) => (
              <Tarjeta
                key={index}
                palabraEsp={item.palabraEsp}
                palabraSue={item.palabraSue}
              />
            ))}
          </div>
        );
      case "sustantivos":
        return (
          <div>
            <h3>Sustantivos</h3>
            {vocabulario.sustantivos.map((item, index) => (
              <Tarjeta
                key={index}
                palabraEsp={item.palabraEsp}
                palabraSue={item.palabraSue}
              />
            ))}
          </div>
        );
      default:
        return (
          <div>

          </div>
        );
    }
  };

  return (
    <div className="vocabulario-container">
      <nav>
        <button onClick={() => setCategoria("verbos")}>Verbos</button>
        <button onClick={() => setCategoria("adjetivos")}>Adjetivos</button>
        <button onClick={() => setCategoria("sustantivos")}>Sustantivos</button>
      </nav>

      <div></div>
    </div>

  );
}

export default Vocabulario;
