const obtenerPalabras = async (categoria) => {
    try {
      // Carga el archivo JSON desde la carpeta "public"
      const response = await fetch("/vocabulario.json");
      const data = await response.json();

      // Retorna las palabras de la categoría solicitada
      return data[categoria] || [];
    } catch (error) {
      console.error("Error al cargar el vocabulario:", error);
      return [];
    }
  };

  export default obtenerPalabras;
