import React, { useState, useEffect } from "react";
import Tarjeta from "./Tarjeta";
import obtenerPalabras from "../services/vocabularioService";
import traducirPalabra from "../services/translateService"; // Importamos la funci贸n de traducci贸n
import "./vocabulario.css";

function Vocabulario() {
  const [palabras, setPalabras] = useState([]);
  const [categoria, setCategoria] = useState("vocabulario");

  useEffect(() => {
    const cargarPalabras = async () => {
      const nuevasPalabras = await obtenerPalabras();
      const palabrasTraducidas = await Promise.all(
        nuevasPalabras.map(async (palabra) => {
          const traduccion = await traducirPalabra(palabra);
          return { palabraEsp: palabra, palabraSue: traduccion };
        })
      );
      setPalabras(palabrasTraducidas);
    };
    cargarPalabras();
  }, []);

  const renderCategoria = () => {
    return (
      <div>
        <h3>Palabras Aleatorias</h3>
        {palabras.map((item, index) => (
          <Tarjeta
            key={index}
            palabraEsp={item.palabraEsp}
            palabraSue={item.palabraSue}
          />
        ))}
      </div>
    );
  };

  return (
    <div className="vocabulario-container">
      <nav>
        <button onClick={() => setCategoria("vocabulario")}>Vocabulario</button>
      </nav>
      {renderCategoria()}
    </div>
  );
}

export default Vocabulario;
