import React, { useState } from 'react';
import './Tarjeta.css';  // Asegúrate de que este archivo CSS esté importado correctamente

function Tarjeta({ palabraEsp, palabraSue }) {
  const [mostrarTraduccion, setMostrarTraduccion] = useState(false);

  const handleClick = () => {
    setMostrarTraduccion(!mostrarTraduccion);
  };

  return (
    <div className="tarjeta" onClick={handleClick}>
      <div className="contenido">
        <p className="palabra">
          {mostrarTraduccion ? palabraSue : palabraEsp}
        </p>
      </div>
    </div>
  );
}

export default Tarjeta;
