import axios from "axios";

// URL de la API de palabras aleatorias (puedes ajustar este número según lo necesites)
const API_URL = "https://random-word-api.herokuapp.com/word?number=50";

// URL de Google Translate API (asegúrate de agregar tu clave API en el endpoint)
const GOOGLE_TRANSLATE_API_URL = "https://translation.googleapis.com/language/translate/v2";
const GOOGLE_API_KEY = "TU_CLAVE_API";  // Reemplaza con tu clave API

// Función para traducir usando Google Translate API
const traducirPalabra = async (palabra, idiomaDestino = "es") => {
  try {
    const response = await axios.post(GOOGLE_TRANSLATE_API_URL, {
      q: palabra,
      target: idiomaDestino,
      key: GOOGLE_API_KEY,
    });
    return response.data.data.translations[0].translatedText;
  } catch (error) {
    console.error("Error al traducir la palabra:", error);
    return palabra; // Si hay error, devolvemos la palabra original
  }
};

// Función para obtener palabras y categorizarlas
const obtenerPalabras = async () => {
  try {
    // Obtener palabras aleatorias
    const response = await axios.get(API_URL);
    const palabras = response.data;

    // Obtener traducciones en español
    const palabrasConTraduccion = await Promise.all(
      palabras.map(async (palabra) => {
        const traduccion = await traducirPalabra(palabra, "es"); // Traducir cada palabra al español
        return { sueco: palabra, espanol: traduccion };
      })
    );

    // Simulación de subcategorías con traducción al español
    const palabrasPorCategoria = {
      verbos: palabrasConTraduccion.filter(palabra => palabra.sueco.endsWith('ar') || palabra.sueco.endsWith('er') || palabra.sueco.endsWith('ir')),
      adjetivos: palabrasConTraduccion.filter(palabra => palabra.sueco.length > 5),
      en_ett: palabrasConTraduccion.filter(palabra => palabra.sueco.startsWith('en') || palabra.sueco.startsWith('ett')),
      otros: palabrasConTraduccion.filter(palabra => !(palabra.sueco.startsWith('en') || palabra.sueco.startsWith('ett')) && palabra.sueco.length <= 5),
    };

    return palabrasPorCategoria;
  } catch (error) {
    console.error("Error al obtener palabras:", error);
    return {
      verbos: [],
      adjetivos: [],
      en_ett: [],
      otros: [],
    };
  }
};

export default obtenerPalabras;
