// src/components/Vocabulario.js
import React, { useState, useEffect } from "react";
import Tarjeta from "./Tarjeta";
import obtenerPalabras from "../services/vocabularioService"; // Importamos el servicio
import "./vocabulario.css";

function Vocabulario() {
  const [palabras, setPalabras] = useState([]);
  const [categoria, setCategoria] = useState("vocabulario");

  // Cargar las palabras de forma automática al montar el componente
  useEffect(() => {
    const cargarPalabras = async () => {
      const nuevasPalabras = await obtenerPalabras();
      setPalabras(nuevasPalabras);
    };
    cargarPalabras();
  }, []);

  // Función para manejar la categoría (puedes expandirlo según necesites)
  const renderCategoria = () => {
    return (
      <div>
        <h3>Palabras Aleatorias</h3>
        {palabras.map((item, index) => (
          <Tarjeta
            key={index}
            palabraEsp={item}  // Si se está usando un API que devuelve solo palabras, puedes adaptarlo.
            palabraSue={item}  // Aquí deberías buscar la traducción al sueco
          />
        ))}
      </div>
    );
  };

  return (
    <div className="vocabulario-container">
      <nav>
        <button onClick={() => setCategoria("vocabulario")}>Vocabulario</button>
      </nav>
      {renderCategoria()}
    </div>
  );
}

export default Vocabulario;
