import axios from "axios";

// URL de la API para obtener palabras aleatorias
const API_URL = "https://random-word-api.herokuapp.com/word?number=50";

// Tu clave de API de Yandex (reemplaza con tu clave real)
const YANDEX_API_KEY = "TU_CLAVE_DE_API_YANDEX";  // Obtén esta clave al registrarte

// URL de Yandex Translate
const TRANSLATE_API_URL = `https://translate.yandex.net/api/v1.5/tr.json/translate?key=${YANDEX_API_KEY}`;

// Función para traducir la palabra de sueco a español usando Yandex
const traducirPalabra = async (palabraSueco) => {
  try {
    const response = await axios.post(TRANSLATE_API_URL, null, {
      params: {
        text: palabraSueco,
        lang: "sv|es", // de sueco a español
      },
    });
    return response.data.text[0];  // Retorna la traducción
  } catch (error) {
    console.error("Error al traducir palabra:", error);
    return "Traducción no disponible";  // Si hay un error, retorna un mensaje predeterminado
  }
};

// Función para obtener palabras y sus traducciones
const obtenerPalabras = async () => {
  try {
    // Obtener palabras aleatorias
    const response = await axios.get(API_URL);
    const palabras = response.data;

    // Crear un array de palabras con su traducción
    const palabrasConTraduccion = await Promise.all(
      palabras.map(async (palabra) => {
        const traduccion = await traducirPalabra(palabra);
        return {
          sueco: palabra,
          espanol: traduccion, // Aquí se agrega la traducción al español
        };
      })
    );

    // Categorizar las palabras en subcategorías
    const palabrasPorCategoria = {
      verbos: palabrasConTraduccion.filter(palabra => palabra.sueco.endsWith('ar') || palabra.sueco.endsWith('er') || palabra.sueco.endsWith('ir')),
      adjetivos: palabrasConTraduccion.filter(palabra => palabra.sueco.length > 5),
      en_ett: palabrasConTraduccion.filter(palabra => palabra.sueco.startsWith('en') || palabra.sueco.startsWith('ett')),
      otros: palabrasConTraduccion.filter(palabra => !(palabra.sueco.startsWith('en') || palabra.sueco.startsWith('ett')) && palabra.sueco.length <= 5),
    };

    return palabrasPorCategoria;
  } catch (error) {
    console.error("Error al obtener palabras:", error);
    return {
      verbos: [],
      adjetivos: [],
      en_ett: [],
      otros: [],
    };
  }
};

export default obtenerPalabras;
