// src/components/Vocabulario.js
import React, { useState, useEffect } from "react";
import obtenerPalabras from "../services/vocabularioService";  // Importa el servicio
import Tarjeta from "./Tarjeta";
import "./vocabulario.css";

function Vocabulario() {
  const [palabras, setPalabras] = useState({
    verbos: [],
    adjetivos: [],
    en_ett: [],
    otros: []
  });

  useEffect(() => {
    const cargarPalabras = async () => {
      const nuevasPalabras = await obtenerPalabras();
      // Si no hay palabras, asegurar que se mantengan como arrays vacíos
      setPalabras(nuevasPalabras || { verbos: [], adjetivos: [], en_ett: [], otros: [] });
    };
    cargarPalabras();
  }, []);

  return (
    <div className="vocabulario-container">
      <h3>Vocabulario por Categoría</h3>

      <div className="categoria">
        <h4>Verbos</h4>
        <div className="palabras-columnas">
          {palabras.verbos.length > 0 ? (
            palabras.verbos.map((palabra, index) => (
              <Tarjeta key={index} palabraEsp={palabra.espanol} palabraSue={palabra.sueco} />
            ))
          ) : (
            <p>No hay verbos disponibles</p>
          )}
        </div>
      </div>

      <div className="categoria">
        <h4>Adjetivos</h4>
        <div className="palabras-columnas">
          {palabras.adjetivos.length > 0 ? (
            palabras.adjetivos.map((palabra, index) => (
              <Tarjeta key={index} palabraEsp={palabra.espanol} palabraSue={palabra.sueco} />
            ))
          ) : (
            <p>No hay adjetivos disponibles</p>
          )}
        </div>
      </div>

      <div className="categoria">
        <h4>Palabras en 'En' o 'Ett'</h4>
        <div className="palabras-columnas">
          {palabras.en_ett.length > 0 ? (
            palabras.en_ett.map((palabra, index) => (
              <Tarjeta key={index} palabraEsp={palabra.espanol} palabraSue={palabra.sueco} />
            ))
          ) : (
            <p>No hay palabras en 'En' o 'Ett' disponibles</p>
          )}
        </div>
      </div>

      <div className="categoria">
        <h4>Otros</h4>
        <div className="palabras-columnas">
          {palabras.otros.length > 0 ? (
            palabras.otros.map((palabra, index) => (
              <Tarjeta key={index} palabraEsp={palabra.espanol} palabraSue={palabra.sueco} />
            ))
          ) : (
            <p>No hay otras palabras disponibles</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Vocabulario;
