import React, { useState, useEffect } from "react";
import obtenerPalabras from "../services/vocabularioService";  // Importa el servicio
import Tarjeta from "./Tarjeta";
import "./vocabulario.css";

function Vocabulario() {
  const [palabras, setPalabras] = useState({
    verbos: [],
    adjetivos: [],
    en_ett: [],
    otros: []
  });

  useEffect(() => {
    const cargarPalabras = async () => {
      const nuevasPalabras = await obtenerPalabras();
      // Asegúrate de que el objeto contiene arrays vacíos si las categorías están vacías
      setPalabras(nuevasPalabras || { verbos: [], adjetivos: [], en_ett: [], otros: [] });
    };
    cargarPalabras();
  }, []);

  const renderCategoria = (categoria, nombre) => {
    return (
      <div className="categoria">
        <h4>{nombre}</h4>
        <div className="palabras-columnas">
          {Array.isArray(categoria) && categoria.length > 0 ? (
            // Dividir las palabras en filas de 5
            categoria.slice(0, 5).map((palabra, index) => (
              <div key={index} className="palabra-fila">
                <Tarjeta palabraEsp={palabra.espanol} palabraSue={palabra.sueco} />
              </div>
            ))
          ) : (
            <p>No hay {nombre.toLowerCase()} disponibles</p>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="vocabulario-container">
      <h3>Vocabulario por Categoría</h3>

      {renderCategoria(palabras.verbos, "Verbos")}
      {renderCategoria(palabras.adjetivos, "Adjetivos")}
      {renderCategoria(palabras.en_ett, "Palabras en 'En' o 'Ett'")}
      {renderCategoria(palabras.otros, "Otros")}
    </div>
  );
}

export default Vocabulario;
