// src/services/vocabularioService.js
import axios from "axios";

// URL de la API (puedes cambiarla si tienes acceso a una que devuelva tanto sueco como español)
const API_URL = "https://random-word-api.herokuapp.com/word?number=50";  // Ajusta el número de palabras

const obtenerPalabras = async () => {
  try {
    const response = await axios.get(API_URL);
    const palabras = response.data;

    // Simulación de subcategorías con traducción al español
    const palabrasPorCategoria = {
      verbos: palabras.filter(palabra => palabra.endsWith('ar') || palabra.endsWith('er') || palabra.endsWith('ir')).map(palabra => ({
        sueco: palabra,
        espanol: "Verbo en español" // Aquí podrías agregar una traducción real, si tienes acceso a una API
      })),
      adjetivos: palabras.filter(palabra => palabra.length > 5).map(palabra => ({
        sueco: palabra,
        espanol: "Adjetivo en español" // Lo mismo, podrías agregar traducciones reales
      })),
      en_ett: palabras.filter(palabra => palabra.startsWith('en') || palabra.startsWith('ett')).map(palabra => ({
        sueco: palabra,
        espanol: "Palabra en español" // Traducción simulada
      })),
      otros: palabras.filter(palabra => !palabra.startsWith('en') && !palabra.startsWith('ett') && palabra.length <= 5).map(palabra => ({
        sueco: palabra,
        espanol: "Palabra común en español" // Traducción simulada
      }))
    };

    return palabrasPorCategoria;
  } catch (error) {
    console.error("Error al obtener palabras:", error);
    return {
      verbos: [],
      adjetivos: [],
      en_ett: [],
      otros: []
    };
  }
};

export default obtenerPalabras;
